import React, { useState } from "react";
import { reservationData } from "../data";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import TimePicker from "react-time-picker";
import "../timepicker.css";
import {
  FaUsers,
  FaCalendar,
  FaClock,
} from "react-icons/fa";
import { motion } from "framer-motion";
import {
  fadeIn,
  staggerContainer,
} from "../variants";

const Reservation = () => {
  const {
    title,
    subtitle,
    modelImg,
    btnText,
  } = reservationData;
  const [startDate, setStartDate] =
    useState(new Date());
  const [value, setValue] =
    useState("10:00");
  return (
    <section className="relative top-96 z-30 pb-20 lg:py-[100px]">
      <div className="container mx-auto">
        <div className="text-center">
          <h2 className="h2 capitalize">
            {title}
          </h2>
          <p className="mb-8 text-dark">
            {subtitle}
          </p>
          <div className="flex justify-center mb-8">
            <img
              src={modelImg}
              alt=""
            />
          </div>
        </div>
        <form>
          <div className="flex flex-col lg:flex-row gap-y-4 items-center justify-between mb-8">
            <div>
              <div className="flex items-center gap-x-[10px] font-semibold text-dark text-base mb-3">
                <FaCalendar />
                <div>Choose Date</div>
              </div>
              <DatePicker
                className="input"
                selected={startDate}
                onChange={(date) =>
                  setStartDate(date)
                }
              />
            </div>
            <div>
              <div className="flex items-center gap-x-[10px] font-semibold text-dark text-base mb-3">
                <FaClock />
                <div>Choose Time</div>
              </div>
              <TimePicker
                className="input"
                clearIcon={false}
                clockIcon={false}
                onChange={setValue}
                value={value}
              />
            </div>
            <div>
              <div className="flex items-center gap-x-[10px] font-semibold text-dark text-base mb-3">
                <FaUsers />
                <div>
                  How many people?
                </div>
              </div>
              <input
                className="input"
                type="text"
                placeholder="1"
              />
            </div>
          </div>
          <div className="max-w-[316px] mx-auto flex justify-center">
            <button className="btn capitalize w-full lg:w-auto">
              {btnText}
            </button>
          </div>
        </form>
      </div>
    </section>
  );
};

export default Reservation;
