import React from "react";

import { menuData } from "../data";

import { motion } from "framer-motion";

import {
  fadeIn,
  staggerContainer,
} from "../variants";

const Menu = () => {
  const {
    title,
    subtitle,
    modelImg,
    menuItems,
    btnText,
  } = menuData;
  return (
    <section className="min-h-[780px]">
      <div className=" h-[780px] bg-menu absolute w-full -z-0 max-w-[1800px]"></div>
      <div className="relative z-10 top-72 lg:top-52">
        <div className="container mx-auto flex flex-col items-center text-center">
          <h2 className="h2 capitalize text-white max-w-[400px] text-center">
            {title}
          </h2>
          <p className="text-white/70 capitalize mb-8">
            {subtitle}
          </p>
          <div>
            <img
              src={modelImg}
              alt=""
            ></img>
          </div>
        </div>
      </div>
      <div className="relative top-80 lg:top-96">
        <div className="container mx-auto">
          <div>
            <div>
              {menuItems.map(
                (item, index) => {
                  const {
                    image,
                    name,
                    price,
                    description,
                  } = item;
                  return (
                    <div key={index}>
                      <div className="flex flex-col">
                        <div>
                          <img
                            src={image}
                            alt=""
                          />
                        </div>
                        <div>
                          <div>
                            {name}
                          </div>
                          <div>
                            {price}
                          </div>
                          <div>
                            {
                              description
                            }
                          </div>
                        </div>
                      </div>
                    </div>
                  );
                }
              )}
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Menu;
